llm_command:
bash
kubectl patch networkpolicy cartservice-policy -n default --type='json' -p='[{
  "op": "add",
  "path": "/spec",
  "value": {
    "podSelector": {
      "matchLabels": {
        "app": "cartservice"
      }
    },
    "policyTypes": ["Egress"],
    "egress": [
      {
        "to": [
          {
            "podSelector": {
              "matchLabels": {
                "app": "redis-cart"
              }
            }
          }
        ],
        "ports": [
          {
            "protocol": "TCP",
            "port": 6379
          }
        ]
      }
    ]
  }
}]'
