llm_command:
bash
kubectl patch networkpolicy allow-frontend-checkout-to-shipping -n default --type='merge' -p='{
  "spec": {
    "podSelector": {
      "matchLabels": {
        "app": "shippingservice"
      }
    },
    "ingress": [
      {
        "from": [
          {
            "namespaceSelector": {
              "matchLabels": {
                "name": "default"
              }
            },
            "podSelector": {
              "matchLabels": {
                "app": "frontend"
              }
            }
          },
          {
            "namespaceSelector": {
              "matchLabels": {
                "name": "default"
              }
            },
            "podSelector": {
              "matchLabels": {
                "app": "checkoutservice"
              }
            }
          }
        ],
        "ports": [
          {
            "protocol": "TCP",
            "port": 50051
          }
        ]
      }
    ]
  }
}'
