llm_command:
bash
kubectl patch networkpolicy allow-egress-to-email-and-payment -n default --type='json' -p='[
  {
    "op": "add",
    "path": "/spec",
    "value": {
      "podSelector": {
        "matchLabels": {
          "app": "checkoutservice"
        }
      },
      "policyTypes": ["Egress"],
      "egress": [
        {
          "to": [
            {
              "podSelector": {
                "matchLabels": {
                  "app": "emailservice"
                }
              }
            }
          ],
          "ports": [
            {
              "protocol": "TCP",
              "port": 5000
            }
          ]
        },
        {
          "to": [
            {
              "podSelector": {
                "matchLabels": {
                  "app": "paymentservice"
                }
              }
            }
          ],
          "ports": [
            {
              "protocol": "TCP",
              "port": 50051
            }
          ]
        }
      ]
    }
  }
]'
