llm_command:
bash
kubectl patch networkpolicy checkoutservice -n checkoutservice --type=json -p='[
  {
    "op": "add",
    "path": "/spec/ingress",
    "value": [
      {
        "from": [
          {
            "podSelector": {
              "matchLabels": {
                "app": "frontend"
              }
            }
          }
        ],
        "ports": [
          {
            "port": 5050,
            "protocol": "TCP"
          }
        ]
      }
    ]
  }
]'
